jQuery.get("https://ipinfo.io", function(response) {
	if(response.region == 'England'){
		jQuery('.price-area').removeClass('active');
		jQuery('.price-area2').addClass('active');
		jQuery('.item-toggle').addClass('money-sign');
	}else{
		jQuery('.price-area').addClass('active');
		jQuery('.price-area2').removeClass('active');
		jQuery('.item-toggle').removeClass('money-sign');
	}	
}, "jsonp");

